import{_ as p,o as e,c as _,f as t}from"./app.5636a2db.js";const a={},c=t('<h1 id="\u7C89\u7D05\u7761\u8863\u5973\u9B3C\u7684\u7955\u5BC6" tabindex="-1"><a class="header-anchor" href="#\u7C89\u7D05\u7761\u8863\u5973\u9B3C\u7684\u7955\u5BC6" aria-hidden="true">#</a> \u7C89\u7D05\u7761\u8863\u5973\u9B3C\u7684\u7955\u5BC6</h1><p>\u25CE\u8AAA\u8D77\u963F\u529B\u53D4\u53D4\uFF0C\u5728\u5927\u9F8D\u5CD2\u7B97\u662F\u5C0F\u6709\u540D\u6C23\u3002\u4ED6\u5C31\u4F4F\u5728\u96E2\u6211\u5BB6\u7D04\u56DB\u9593\u623F\u5C4B\u9060\u7684\u8DDD\u96E2\uFF0C\u6642\u5E38\u5728\u4E09\u9EDE\u534A\u5DE6\u53F3\uFF0C\u7761\u5728\u9D3F\u820D\u7684\u6211\uFF0C\u5C31\u6703\u88AB\u4ED6\u6FC3\u6FC3\u6696\u6696\u7684\u8C46\u6F3F\u9999\u7D66\u8A98\u9192\uFF1B\u5495\u5695\u3001\u5495\u5695\u7684\u809A\u5B50\uFF0C\u4E00\u5B9A\u5F97\u5148\u6367\u500B\u7897\u3001\u8DD1\u53BB\u4ED6\u90A3\u8CB7\u584A\u8C46\u8150\u4F86\u5403\uFF0C\u624D\u80FD\u6B62\u4F4F\u809A\u5B50\u7684\u300E\u592D\u98FD\u5435\u300F\uFF08\u53F0\u8A9E\uFF09\u3002</p><p>\u8CE3\u8C46\u8150\u7684\u963F\u529B\u53D4\u53D4\uFF0C\u5BB6\u88E1\u9084\u8A2D\u6709\u300C\u795E\u58C7\u300D\uFF0C\u4F9B\u4E86\u4E00\u5927\u5806\u795E\u50CF\uFF0C\u5F88\u591A\u4EBA\u90FD\u6703\u4F86\u4ED6\u7684\u795E\u58C7\u554F\u4E8B\u3001\u62DC\u62DC\uFF0C\u4ED6\u7684\u8001\u5A46\uFF0C\u6700\u6703\u5230\u8655\u8A87\u8000\u81EA\u5DF1\u5BB6\u7684\u795E\u58C7\u300C\u591A\u6E96\u3001\u591A\u9748\u9A57\u300D\uFF1B\u7576\u5979\u7A81\u7136\u5F97\u4E86\u985E\u4F3C\u76AE\u819A\u764C\u7684\u602A\u75C5\u6642\uFF0C\u5979\u9589\u9580\u5728\u5BB6\u6C92\u5E7E\u500B\u661F\u671F\uFF0C\u8857\u574A\u9130\u5C45\u6B63\u5947\u602A\u8457\u963F\u529B\u5AC2\u600E\u9EBC\u4E0D\u898B\u4E86\uFF1F\u5C31\u50B3\u51FA\u5979\u7684\u6B7B\u8A0A\u3002</p><p>\u7576\u6642\uFF0C\u5927\u5BB6\u90FD\u5F88\u8A1D\u7570\u5979\u6B7B\u5F97\u9019\u9EBC\u7A81\u7136\uFF0C\u9023\u5C0F\u5B69\u5B50\u90FD\u6703\u554F\u963F\u529B\uFF1A\u300C\u963F\u529B\u53D4\u53D4\uFF0C\u4F60\u5BB6\u7684\u795E\u58C7\u3001\u795E\u660E\u4E0D\u662F\u5F88\u9748\u55CE\uFF1F\u70BA\u4EC0\u9EBC\u4F60\u6CBB\u4E0D\u597D\u4F60\u8001\u5A46\u5462\uFF1F\u300D\u963F\u529B\u53EA\u662F\u82E6\u8457\u81C9\u8AAA\uFF1A\u300C\u56E1\u4ED4\u4EBA\uFF0C\u4E0D\u8981\u554F\u90A3\u9EBC\u591A\uFF01\u300D</p><p>\u8001\u5A46\u904E\u4E16\u4EE5\u5F8C\uFF0C\u963F\u529B\u672C\u4F86\u9084\u6709\u4E00\u5B50\u4E00\u5973\uFF08\u90FD\u6210\u5E74\u4E86\uFF09\u4F4F\u5728\u4E00\u8D77\u3002\u4E0D\u6599\u3001\u5148\u662F\u5973\u5152\u96E2\u5947\u81EA\u6BBA\u6B7B\u4EA1\uFF0C\u9694\u5E74\u53C8\u662F\u5152\u5B50\u4E00\u6A21\u4E00\u6A23\u7684\u96E2\u5947\u6B7B\u6CD5\u2014\u5F9E\u6B64\u3001\u963F\u529B\u5C31\u6210\u4E86\u7368\u81EA\u4E00\u4EBA\uFF0C\u9867\u8457\u4ED6\u7684\u795E\u58C7\u548C\u8C46\u8150\u6524\u3002</p><p>\u5728\u5927\u9F8D\u5CD2\uFF0C\u53EA\u8981\u767C\u751F\u547D\u6848\uFF0C\u6D3E\u51FA\u6240\u7684\u8B66\u5BDF\u4E00\u5B9A\u5148\u627E\u4EBA\u4F86\u53EB\u5C0F\u80D6\u904E\u53BB\u2014\u53EF\u4E0D\u662F\u6211\u6709\u5ACC\u7591\u554A\uFF01\u800C\u662F\u56E0\u70BA\u5728\u5927\u9F8D\u5CD2\uFF0C\u6C92\u6709\u6211\u4E0D\u8A8D\u8B58\u7684\u4EBA\uFF0C\u8B66\u5BDF\u90FD\u6703\u53EB\u6211\u53BB\u6307\u8A8D\u6B7B\u8005\u7684\u8EAB\u5206\uFF1A\u300C\u5C0F\u80D6\uFF0C\u4F60\u4F86\u770B\u770B\u3001\u9019\u500B\u4F60\u77E5\u9053\u662F\u8AB0\u55CE\uFF1F\u300D\u5F9E\u5C0F\u751F\u5728\u6D41\u6C13\u4E4B\u5BB6\u7684\u6211\uFF0C\u898B\u6163\u5EDD\u6BBA\u3001\u8840\u8165\u7684\u5834\u9762\uFF0C\u8A8D\u5C4D\u4E00\u9EDE\u4E5F\u5687\u4E0D\u5012\u6211\uFF0C\u6211\u7E3D\u662F\u80FD\u6E96\u78BA\u5730\u544A\u8A34\u8B66\u5BDF\uFF1A\u300C\u9019\u500B\u662F\u4F4F\u5728\xD7\xD7\u7684\xD7\xD7\xD7\u2026\u2026\u300D\u4E5F\u56E0\u6B64\u6D3E\u51FA\u6240\u7684\u8B66\u5BDF\u90FD\u5F88\u559C\u6B61\u6211\uFF0C\u8B66\u6C11\u5408\u4F5C\u7121\u9593\uFF01</p><p>\u8A18\u5F97\u90A3\u4E00\u6B21\uFF0C\u5F9E\u5E02\u5834\u8CE3\u5B8C\u65E9\u9910\u7CA5\u51FA\u4F86\uFF0C\u807D\u8AAA\u6211\u5BB6\u9644\u8FD1\u6DE1\u6C34\u6CB3\u908A\u6709\u6D6E\u5C4D\u7684\u6D88\u606F\u3002\u6211\u98C6\u8457\u8173\u8E0F\u8ECA\u3001\u706B\u901F\u8D95\u5230\u6848\u767C\u73FE\u5834\u2026\u2026\u8B66\u54E1\u4E00\u770B\u898B\u6211\u5C31\u558A\u8457\uFF1A\u300C\u5C0F\u80D6\uFF0C\u6211\u624D\u53EB\u4EBA\u53BB\u627E\u4F60\u4F86\u5414\uFF0C\u5FEB\u904E\u4F86\u770B\u770B\uFF0C\u8A8D\u5F97\u9019\u5169\u500B\u4EBA\u55CE\uFF1F\u300D\u6211\u4E00\u770B\u3001\u9019\u5169\u500B\u6D6E\u5C4D\u662F\u4E00\u7537\u4E00\u5973\uFF0C\u7528\u7D05\u7DDA\u628A\u5F7C\u6B64\u7D81\u5728\u4E00\u8D77\uFF0C\u6CE1\u4E86\u6C34\u3001\u816B\u8139\u6158\u767D\u7684\u5C4D\u9AD4\uFF0C\u4E00\u6642\u9084\u633A\u96E3\u8FA8\u8B58\u2026\u2026\u6211\u4ED4\u7D30\u7AEF\u8A73\u4E86\u597D\u4E45\uFF0C\u624D\u8A8D\u51FA\u7ADF\u7136\u662F\u963F\u529B\u53D4\u53D4\u7684\u5973\u5152\uFF01\u800C\u7537\u7684\u662F\u4FEE\u7406\u8173\u8E0F\u8ECA\u7684\uFF08\u662F\u5916\u5730\u4EBA\uFF0C\u5728\u5927\u9F8D\u5CD2\u8DEF\u53E3\u5E6B\u4EBA\u4FEE\u8ECA\uFF09\uFF0C\u5927\u5BB6\u90FD\u53EB\u4ED6\u300C\u5C07\u8ECD\u300D\u3002\u8A8D\u51FA\u6B7B\u8005\u8EAB\u5206\u5F8C\uFF0C\u8173\u529B\u597D\u7684\u5C0F\u80D6\uFF0C\u9084\u5F97\u8CA0\u8CAC\u53BB\u901A\u77E5\u5BB6\u5C6C\u4F86\u9818\u5C4D\u3002</p><p>\u9694\u5E74\uFF0C\u540C\u6A23\u7684\u5730\u9EDE\uFF0C\u53C8\u51FA\u73FE\u5169\u5177\u6D6E\u5C4D\uFF01\u540C\u6A23\u53C8\u662F\u7528\u7D05\u7DDA\u7D81\u5728\u4E00\u8D77\u7684\u7537\u5973\uFF01\u7537\u7684\u7ADF\u7136\u662F\u963F\u529B\u53D4\u53D4\u50C5\u5B58\u7684\u5152\u5B50\uFF01\u6211\u770B\u5F97\u6BDB\u9AA8\u609A\u7136\uFF0C\u5FC3\u88E1\u6697\u60F3\u95DC\u65BC\u963F\u529B\u53D4\u53D4\u5BB6\u7684\u4E8B\uFF0C\u7D55\u5C0D\u4E0D\u662F\u5DE7\u5408\u3002</p><p>\u7576\u6642\u5728\u5730\u4EBA\uFF0C\u90FD\u5728\u8AC7\u8AD6\u963F\u529B\u5BB6\u7684\u6158\u4E8B\u3002</p><p>\u4E4B\u524D\u3001\u963F\u529B\u5E38\u8CE3\u8C46\u8150\u7D66\u5EDF\u88E1\u7684\u51FA\u5BB6\u4EBA\uFF0C\u5F8C\u4F86\u5F9E\u5EDF\u88E1\u8ACB\u56DE\u5BB6\u597D\u5E7E\u5C0A\u795E\u4F9B\u5949\uFF1A\u6709\u5ABD\u7956\u3001\u89C0\u97F3\u3001\u95DC\u516C\u3001\u4E09\u592A\u5B50\u7B49\u597D\u5E7E\u7A2E\u795E\u50CF\uFF0C\u963F\u529B\u89BA\u5F97\u751F\u610F\u6709\u8CFA\u9322\uFF0C\u5C31\u8ACB\u66F4\u591A\u795E\u56DE\u5BB6\u62DC\uFF0C\u9084\u6210\u4E86\u7D66\u4EBA\u554F\u4E8B\u7684\u795E\u58C7\u3002</p><p>\u4ED6\u5BB6\u525B\u597D\u662F\u5728\u5927\u58D5\u6E9D\u65C1\uFF0C\u5C4B\u5B50\u5468\u570D\u7A2E\u6EFF\u4E86\u7AF9\u5B50\uFF0C\u6BCF\u6B21\u665A\u4E0A\u8001\u7238\u53EB\u6211\u53BB\u8CB7\u9152\uFF0C\u90FD\u5F97\u5F9E\u4ED6\u9580\u53E3\u7D93\u904E\u3002\u9ED1\u6F06\u6F06\u7684\u591C\u8272\u88E1\uFF0C\u53EA\u898B\u7AF9\u6797\u9670\u68EE\u68EE\u5730\u6416\u64FA\u8457\uFF0C\u6C99\u6C99\u7684\u8072\u97FF\uFF0C\u5BB3\u5F97\u6211\u8D77\u4E00\u8EAB\u96DE\u76AE\u7599\u7629\u2026\u2026\u5C24\u5176\u8DEF\u908A\u7684\u58D5\u6E9D\u53C8\u6C92\u8B77\u6B04\uFF0C\u6211\u4E5F\u64D4\u5FC3\u6703\u4E0D\u5C0F\u5FC3\u6ED1\u4E0B\u53BB\uFF0C\u9760\u5DE6\u8D70\u4E0D\u5B89\u3001\u9760\u53F3\u8D70\u4E0D\u59A5\u3001\u53EA\u597D\u9078\u8DEF\u4E2D\u9593\u76F4\u8D70\u3001\u5FEB\u901F\u901A\u904E\u2026\u2026</p><p>\u6709\u597D\u5E7E\u6B21\uFF0C\u6211\u770B\u898B\u50CF\u4EBA\u5F62\u7684\u9ED1\u7070\u5F71\u5B50\uFF0C\u6726\u6726\u6727\u6727\u3001\u96E2\u5730\u7D04\u4E09\u5BF8\u9AD8\u5730\u98C4\u9032\u963F\u529B\u7684\u795E\u58C7\u3002\u96D6\u7136\u5FC3\u88E1\u6709\u9EDE\u767C\u6BDB\uFF0C\u4F46\u300C\u9B3C\u300D\u6211\u5E38\u770B\u5230\uFF0C\u5012\u4E5F\u5687\u4E0D\u5230\u6211\u3002\u8A18\u5F97\u5F9E\u524D\u6211\u8DDF\u963F\u5B24\u8B1B\u6211\u770B\u898B\u9019\u7A2E\u9748\u7570\u9B3C\u7269\u7684\u4E8B\uFF0C\u963F\u5B24\u6559\u6211\u3010\u4E0D\u5FC5\u7406\u6703\u3001\u4E0D\u8981\u8B1B\u3001\u7576\u6C92\u770B\u5230\u3011\u5C31\u597D\uFF0C\u6240\u4EE5\u6211\u90FD\u9019\u6A23\u61C9\u5C0D\u770B\u898B\u7684\u9B3C\u7269\uFF0C\u4E00\u76F4\u76F8\u5B89\u7121\u4E8B\u3002</p><p>\u5947\u602A\u7684\u662F\uFF0C\u4F4F\u963F\u529B\u5BB6\u65C1\u908A\u7684\u5E7E\u6236\u4EBA\u5BB6\uFF0C\u5E7E\u4E4E\u6BCF\u6236\u90FD\u51FA\u500B\u7CBE\u795E\u75C5\u4EBA\uFF0C\u9023\u66FF\u963F\u529B\u5BB6\u7684\u300C\u795E\u58C7\u300D\u8D77\u4E69\u8FA6\u4E8B\u7684\u4E69\u7AE5\uFF0C\u4E5F\u662F\u4F4F\u9694\u58C1\u7684\u6BDB\u69AE\u4ED6\u5927\u59D0\uFF1B\u6BDB\u69AE\u81EA\u5DF1\u4E5F\u662F\u7CBE\u795E\u7570\u5E38\uFF0C\u4ED6\u7684\u5927\u59D0\u662F\u7A81\u7136\u300C\u88AB\u5ABD\u7956\u9644\u8EAB\u300D\uFF0C\u4E4B\u5F8C\u5C31\u5728\u963F\u529B\u7684\u795E\u58C7\u7576\u8D77\u4E69\u7AE5\u3002</p><p>\u6211\u66FE\u53BB\u770B\u904E\u5979\u5728\u795E\u58C7\u8D77\u99D5\u7684\u60C5\u5F62\u3002\u6211\u4E0D\u8A8D\u70BA\u90A3\u4E9B\u81EA\u7A31\u300C\u5ABD\u7956\u3001\u89C0\u97F3\u3001\u4E09\u592A\u5B50\u3001XX\u795E\u660E\u300D\u964D\u81E8\u7684\u300C\u795E\u9748\u300D\u662F\u4EC0\u9EBC\u597D\u6771\u897F\u2014\u4E69\u7AE5\u5728\u88AB\u9644\u8EAB\u4E4B\u524D\uFF0C\u96D9\u624B\u6276\u8457\u684C\u6CBF\u3001\u5F4E\u8457\u8170\u3001\u4F4E\u8457\u982D\u5728\u7B49\u795E\u660E\u964D\u81E8\u2026\u2026\u6211\u89AA\u773C\u770B\u898B\u5F9E\u58D5\u6E9D\u4E0A\u4F86\u4E00\u5718\u3001\u4E00\u5718\u9ED1\u7070\u7070\u7684\u6771\u897F\uFF0C\u6CBF\u8457\u5730\u9762\u98C4\u5230\u4E69\u7AE5\u65C1\u908A\uFF0C\u4E69\u7AE5\u5C31\u958B\u59CB\u6416\u982D\u6643\u8166\u5730\u6296\u52D5\u8EAB\u8EC0\u2026\u2026\uFF08\u9019\u662F\u4EBA\u9AD4\u5FC3\u81DF\u90E8\u4F4D\u88AB\u96FB\u78C1\u6CE2\u5E72\u64FE\u7684\u60C5\u5F62\u3002\uFF09</p><p>\u9019\u6642\u963F\u529B\u5C31\u6703\u558A\u8457\uFF1A\u300C\u795E\u660E\u4F86\u4E86\u3001\u795E\u660E\u4F86\u4E86\uFF01\u300D\u4ED6\u6703\u71D2\u4E00\u5927\u628A\u7684\u9999\uFF0C\u5F04\u5F97\u6574\u5C4B\u5B50\u7159\u9727\u7030\u6F2B\uFF0C\u800C\u4E00\u5768\u9ED1\u7070\u7070\u7684\u6771\u897F\uFF0C\u5C31\u6703\u8D81\u9019\u500B\u6642\u5019\uFF0C\u6CBF\u8457\u4E69\u7AE5\u7684\u8173\u6EFE\u4E0A\u8EAB\u3001\u947D\u9032\u5979\u7684\u9F3B\u5B54\uFF1B\u63A5\u8457\u5979\u7684\u5634\u5DF4\u6703\u50CF\u9B5A\u4E00\u6A23\uFF0C\u4E00\u958B\u4E00\u95D4\u5730\u767C\u51FA\u300C\u5575\u3001\u5575\u300D\u8072\uFF0C\u6DF7\u8457\u5589\u982D\u53E3\u6C34\u7684\u5495\u5665\uFF0C\u7136\u5F8C\u602A\u8072\u602A\u8ABF\u5730\u5531\u8D77\u6B4C\u4ED4\u6232\u2026\u2026\u963F\u529B\u6703\u8ACB\u793A\u554F\u9053\uFF1A\u300C\u662F\u4F55\u795E\uFF1F\u300D\u518D\u4F86\u963F\u529B\u5C31\u6703\u8AAA\u4ECA\u5929\u662F\u300C\u5ABD\u7956\u300D\u964D\u81E8\u3001\u6709\u6642\u53C8\u662F\u4EC0\u9EBC\u300C\u4E2D\u58C7\u5143\u5E25\u300D\u3001\u300C\u89C0\u97F3\u5927\u58EB\u300D\u6216\u300C\u592A\u5B50\u723A\u300D\u4E4B\u985E\u7684\u2014\u4F46\u662F\u6211\u6BCF\u6B21\u770B\u5230\u7684\uFF0C\u90FD\u662F\u5F9E\u58D5\u6E9D\u4E0A\u4F86\u7684\u9ED1\u7070\u5718\uFF1B\u6709\u6642\u9084\u770B\u898B\u4E00\u5718\u9ED1\u7403\u7A81\u7136\u300C\u5316\u8EAB\u6210\u5ABD\u7956\u795E\u50CF\u7684\u6A21\u6A23\u300D\uFF0C\u60E1\u72E0\u72E0\u7684\u76EF\u8457\u6211\u770B\u3002</p><p>\u8AAA\u4F86\u4E5F\u602A\uFF0C\u5C0F\u6642\u5019\u5728\u300C\u5927\u5EDF\u3001\u5C0F\u5EDF\u300D\u770B\u4E69\u7AE5\u8D77\u99D5\uFF0C\u4E5F\u662F\u5982\u540C\u524D\u8FF0\u7684\u60C5\u5F62\u3002\u800C\u4E14\u4E69\u7AE5\u8D77\u4E69\u5F8C\uFF0C\u90FD\u6703\u5169\u773C\u6B7B\u76EF\u8457\u6211\u770B\uFF0C\u9023\u963F\u529B\u795E\u58C7\u7684\u4E69\u7AE5\u4E5F\u662F\u9019\u6A23\uFF0C\u597D\u50CF\u77E5\u9053\u6211\u770B\u5F97\u898B\u300C\u7942\u5011\u300D\u7684\u7955\u5BC6\uFF0C\u76EF\u5F97\u6211\u6E3E\u8EAB\u4E0D\u5C0D\u52C1\u3002\u6211\u8A8D\u70BA\u71D2\u9999\u5F04\u5F97\u6574\u5C4B\u662F\u7159\uFF0C\u662F\u4E00\u7A2E\u969C\u773C\u6CD5\uFF0C\u70BA\u7684\u662F\u4E0D\u8B93\u4EBA\u770B\u898B\u300C\u7942\u5011\u300D\u9019\u4E9B\u9ED1\u7070\u7070\u7684\u6771\u897F\u3002\u800C\u6211\u5728\u898B\u904E\u4E69\u7AE5\u5343\u7BC7\u4E00\u5F8B\u7684\u88AB\u9644\u8EAB\u904E\u7A0B\u5F8C\uFF0C\u6211\u5C31\u6C92\u5565\u8208\u8DA3\u770B\u4E86\u3002</p><p>\u8A71\u8AAA\u56DE\u4F86\u963F\u529B\u53D4\u53D4\u5BB6\u5916\uFF0C\u5927\u58D5\u6E9D\u908A\u534A\u591C\u8001\u662F\u51FA\u73FE\u4E00\u500B\u7A7F\u7C89\u7D05\u7761\u8863\u7684\u5973\u9B3C\u3002\u6BCF\u6B21\u6211\u88AB\u8C46\u6F3F\u9999\u5473\u5F04\u9192\uFF0C\u5927\u7D04\u90FD\u662F\u4E09\u9EDE\u534A\u5DE6\u53F3\uFF0C\u597D\u5E7E\u6B21\u6211\u6367\u8457\u8C46\u8150\u56DE\u5BB6\uFF0C\u7D93\u904E\u58D5\u6E9D\u4E26\u6C92\u770B\u898B\u5979\uFF0C\u7B49\u6211\u4E0A\u4E86\u5C4B\u9802\u9D3F\u820D\u6642\uFF0C\u53C8\u770B\u5230\u5979\u5750\u5728\u90A3\uFF01\u6211\u4E5F\u5F88\u597D\u5947\u5979\u5230\u5E95\u662F\u54EA\u4F86\u7684\u9B3C\uFF1F\u6709\u4E00\u5929\u6211\u7279\u5730\u8E72\u5728\u5C4B\u9802\u76EF\u8457\uFF0C\u9023\u8C46\u8150\u4E5F\u4E0D\u8CB7\u4E86\uFF0C\u7B49\u8457\u770B\u5979\u600E\u9EBC\u51FA\u73FE\u2026\u2026\u7A81\u7136\u6211\u770B\u5230\u53C8\u662F\u9ED1\u7070\u7070\u7684\u4E00\u5718\uFF0C\u5F9E\u963F\u529B\u53D4\u53D4\u7684\u795E\u58C7\u6EFE\u51FA\u4F86\uFF0C\u96E2\u5730\u4E09\u5BF8\u5730\u98C4\u554A\u98C4\uFF0C\u5230\u4E86\u6A4B\u908A\u5C31\u7A81\u7136\u8B8A\u6210\u7C89\u7D05\u7761\u8863\u7684\u5973\u4EBA\uFF01</p><p>\u539F\u4F86\u300C\u5973\u9B3C\u300D\u5C31\u662F\u795E\u58C7\u8DD1\u51FA\u4F86\u7684\uFF01\u96E3\u602A\u6211\u66FE\u770B\u898B\u5169\u4E09\u6B72\u5927\u9AD4\u578B\u7684\u5C0F\u5B69\uFF0C\u65E9\u6668\u4E0D\u5230\u56DB\u9EDE\uFF0C\u5F9E\u795E\u58C7\u90A3\u8DD1\u9032\u8DD1\u51FA\u2014\u5C31\u8DDF\u96DC\u8CA8\u5E97\u9580\u53E3\u73A9\u706B\u7684\u5C0F\u5B69\u4E00\u6A23\uFF01</p><p>\u96DC\u8CA8\u5E97\u8001\u95C6\u5A18\u5E38\u53BB\u963F\u529B\u7684\u795E\u58C7\u554F\u4E8B\uFF0C\u6211\u63D0\u9192\u5979\u8981\u5C0F\u5FC3\u706B\u707D\u7684\u4E8B\u6642\uFF0C\u5979\u9084\u8AAA\u795E\u660E\u4E5F\u6307\u793A\u5979\u300C\u6700\u8FD1\u5BB6\u88E1\u6703\u51FA\u4E8B\u300D\u2014\u4E8B\u5F8C\u771F\u7684\u767C\u751F\u706B\u707D\uFF0C\u5979\u53BB\u8DDF\u6211\u8001\u7238\u544A\u72C0\u3001\u8AA3\u8CF4\u6211\uFF0C\u8AAA\uFF1A\u300C\u5C0F\u80D6\u600E\u9EBC\u6703\u5148\u77E5\u9053\u6703\u6709\u706B\u707D\u767C\u751F\uFF1F\u8A72\u4E0D\u6703\u706B\u662F\u4ED6\u653E\u7684\u5427\uFF1F\u300D\u771F\u7684\u628A\u6211\u6C23\u6B7B\u4E86\uFF01\u6211\u4E5F\u6C7A\u5FC3\u50CF\u963F\u5B24\u6559\u7684\uFF0C\u4E0D\u8B1B\u3001\u4E0D\u7406\u9019\u7A2E\u9748\u7570\u4E8B\u4EF6\u3002</p><p>\u963F\u529B\u53D4\u53D4\u8DDF\u300C\u7C89\u7D05\u7761\u8863\u5973\u9B3C\u300D\u642D\u8A15\u7684\u90A3\u6B21\uFF0C\u4ED6\u4F38\u624B\u60F3\u62C9\u5973\u9B3C\uFF0C\u9080\u5979\u56DE\u4ED6\u5BB6\u7761\uFF0C\u6211\u5728\u5C4B\u9802\u770B\u5F97\u6E05\u6E05\u695A\u695A\uFF0C\u4ED6\u7684\u624B\u6C92\u6709\u78B0\u5230\u5973\u9B3C\uFF0C\u90A3\u500B\u5973\u9B3C\u5C31\u500F\u5730\u6E9C\u4E0B\u58D5\u6E9D\u2014\u800C\u963F\u529B\u53D4\u53D4\u5C31\u50CF\u88AB\u96FB\u5230\u4E00\u6A23\uFF0C\u5446\u633A\u633A\u5730\u3001\u50CF\u88AB\u5438\u8D77\u4F86\u9A30\u7A7A\u5F80\u6A4B\u4E0B\u683D\u4E0B\u53BB\uFF01\u982D\u63D2\u5230\u721B\u6CE5\u88E1\u3001\u96D9\u8173\u671D\u5929\u2026\u2026</p><p>\u963F\u529B\u5728\u5927\u9F8D\u5CD2\u7684\u795E\u58C7\uFF0C\u81EA\u5F9E\u963F\u529B\u6B7B\u5F8C\u5C31\u9019\u6A23\u6536\u5834\u4E86\u3002\u8001\u4E00\u8F29\u7684\u4EBA\u90FD\u8AAA\uFF1A\u3010\u795E\u58C7\u62DC\u7684\u4E0D\u662F\u795E\uFF0C\u5168\u662F\u9B3C\uFF0C\u8981\u662F\u6709\u795E\uFF0C\u600E\u9EBC\u958B\u795E\u58C7\u7684\u963F\u529B\u6108\u62DC\u6B7B\u6108\u591A\uFF1F\u6700\u5F8C\u9023\u4ED6\u81EA\u5DF1\u90FD\u6B7B\u5F97\u9019\u9EBC\u602A\u7570\uFF01\u505A\u4EBA\u9084\u662F\u9760\u81EA\u5DF1\u52AA\u529B\uFF0C\u5225\u5984\u60F3\u795E\u660E\u5E6B\u52A9\uFF0C\u5343\u842C\u5225\u53BB\u62DC\u795E\u6C42\u4F5B\uFF0C\u9760\u81EA\u5DF1\u6700\u5B89\u5168\u5BE6\u5728\u3002\u3011</p><p>\u800C\u6211\u4E5F\u8981\u544A\u8A34\u8B80\u8005\u5F88\u91CD\u8981\u7684\u539F\u5247\uFF1A\u4EBA\u3001\u9B3C\u672C\u4F86\u5C31\u4E92\u4E0D\u5E72\u6D89\uFF0C\u4F60\u4E0D\u6C42\u7942\u3001\u7942\u4E5F\u4E0D\u6703\u60F9\u4F60\uFF0C\u96D6\u7136\u6211\u5F9E\u5C0F\u5C31\u5E38\u770B\u898B\u9019\u4E9B\u9B3C\u985E\uFF0C\u4F46\u6211\u5F9E\u4E0D\u8DDF\u300C\u7942\u5011\u300D\u4EA4\u8AC7\u3001\u6E9D\u901A\uFF0C\u90FD\u7576\u6C92\u770B\u898B\uFF0C\u6240\u4EE5\u7942\u5011\u4E5F\u4E0D\u66FE\u5371\u5BB3\u6211\u3002\u5207\u8A18\uFF01\u4E0D\u7BA1\u4F60\u770B\u898B\u4F5B\u7956\u3001\u9084\u662F\u89C0\u97F3\uFF0C\u751A\u81F3\u770B\u898B\u6B7B\u53BB\u7684\u963F\u516C\u3001\u9084\u662F\u963F\u7956\u2026\u2026\u901A\u901A\u7576\u4F5C\u6C92\u770B\u898B\uFF0C\u4FDD\u8B49\u8B93\u4F60\u842C\u90AA\u4E0D\u4FB5\uFF01</p>',22),o=[c];function r(n,s){return e(),_("div",null,o)}const i=p(a,[["render",r],["__file","06-\u7C89\u7D05\u7761\u8863\u5973\u9B3C\u7684\u7955\u5BC6.html.vue"]]);export{i as default};
