import{_ as n,r as t,o,c as u,d as l,e as i,a,f as s}from"./app-f87c10f2.js";const _={},c=l("h1",{id:"利水渗湿药-8",tabindex:"-1"},[l("a",{class:"header-anchor",href:"#利水渗湿药-8","aria-hidden":"true"},"#"),i(" 利水渗湿药(8)")],-1),r=l("p",null,"通利水道，渗除水湿的药物，通利小便，具有排除停蓄体内水湿之邪的作用，可以解除由水湿停蓄引起的各种病症，并能防止水湿日久化饮，水气凌心",-1),p=l("li",null,[l("p",null,"小便不利、水肿、淋症等病症。对于湿温、黄疸、湿疮")],-1),h=l("li",null,[l("p",null,"多甘、苦、淡，性多寒、平。主要归肾、膀胱经，兼入脾、肺、小肠")],-1),d=l("li",null,[l("p",null,"有偏于利水渗湿、利水消肿、利水通淋以及利湿退黄之不同，应跟据具体病情适当")],-1),f=l("li",null,[l("p",null,"水湿病症，有兼热兼寒之分，应用时需配合清热药与祛寒药同用。如兼有脾虚不足，肾阳亏损者又应配合健脾、补阳药同用")],-1),m=l("li",null,[l("p",null,"对于阴虚不足者应慎用")],-1),x={href:"https://kknews.cc/zh-tw/health/bq26gb6.html",target:"_blank",rel:"noopener noreferrer"},k=s('<h3 id="健脾-清熱利水通淋-利濕退黃" tabindex="-1"><a class="header-anchor" href="#健脾-清熱利水通淋-利濕退黃" aria-hidden="true">#</a> 健脾-清熱利水通淋-利濕退黃</h3><ul><li><p>茯苓苡仁-木通車前子澤瀉燈芯草-地耳草垂盆草</p></li><li><p>茯苓: 甘、淡，平。归心、肺、脾、肾经</p><ul><li>利水渗湿，健脾，化痰，宁心安神</li><li>治小便不利，水肿、脾虚湿聚 <ul><li>偏于寒湿者，可与桂枝、白朮</li><li>偏于湿热者，可与猪苓、泽泻</li><li>脾气虚者，可与党参、黄耆、白朮</li><li>虚寒者，还可配附子、白朮</li></ul></li><li>标本兼顾之效，常与党参、白朮、山药等配伍。有可用为补肺脾，治气虚</li><li>停聚化生痰饮之症，具有治疗作用。可用半夏、陈皮同用，也可配桂枝、白朮同用</li><li>治心悸，失眠，常与人参、远志、酸枣仁</li><li>五苓散（《伤寒论》）：茯苓、猪苓、泽泻、白朮、桂枝。治头痛发热，口燥咽干，烦渴饮水，水入即吐，小便不利</li><li>苓桂朮甘汤（《金匮要略》）：茯苓、桂枝、白朮、炙甘草。治痰饮停聚，头眩，心悸，咳嗽</li><li>指迷茯苓丸（《医方考》）：半夏、茯苓、枳壳、风化硝、生姜。治痰湿内停，流注四肢，肩臂酸痛，两手疲软者</li></ul></li><li><p>薏苡仁: 甘、淡，微寒。归脾、肾、肺经</p><ul><li>利水渗湿，健脾，除痹，能清肺热，下利肠胃湿热</li><li>湿热内蕴之症，对小便短赤，可与滑石、通草</li><li>治脾虚水肿、脚气肿痛，配伍茯苓、白朮、木瓜、吴茱萸</li><li>湿滞皮肉筋脉引起的痹痛拘挛，常与桂枝、苍朮</li><li>治肺痈胸痛、咯吐脓痰可与鲜芦根、冬瓜子、桃仁、鱼腥草</li><li>治肠痈，可与败酱草、附子</li><li>三仁汤（《温病条辨》）：苡仁、白豆蔻、杏仁、竹叶、通草、滑石、半夏、厚朴。治温病出起，头痛恶寒，身重疼痛，舌白不渴，胸闷不饥，午后身热</li></ul></li><li><p>木通: 苦，寒。归心、脾、小肠、膀胱经</p><ul><li>清热利水通淋，清泄心火，通乳，利痹。治小便不利，淋沥涩痛，水肿，脚气</li><li>治湿热下注、淋沥涩痛要药，常与车前子、滑石等同用</li><li>苦寒入心经，利通小便，导热下行降心火，可用于心火上炎、心烦尿赤、口舌生疮等症，常与生地、竹叶、甘草同用</li></ul></li><li><p>车前子: 甘，寒。归肝、肾、小肠、肺经。</p><ul><li>清热利水通淋，渗湿止泻，清肝明目，祛痰止咳</li><li>清肝热而明头目，不论虚实，都可配用</li></ul></li><li><p>泽泻: 甘，寒。归肾、膀胱经</p><ul><li>利水渗湿，泄热</li><li>利水作用与茯苓相似，亦为利水渗湿常用之品，寒凉，能泄肾与膀胱之热，故对水湿偏热者，尤为适宜</li><li>肾阴不足、虚火亢盛，配地黄、山茱萸等同用，有泻泄相火</li><li>泽泻汤（《金匮要略》）：泽泻、白朮。治心下有支饮，其人苦眩冒者；亦治水泻小便短少者</li></ul></li><li><p>灯心草: 甘、淡，微寒。归心、肺、小肠经。</p><ul><li>清热利水，清心除烦。</li><li>药力较薄弱，适宜病情较轻者，或作清热利水药如木通、滑石药的辅助品</li></ul></li><li><p>猪苓: 甘、淡，平。归肾、膀胱经</p><ul><li>利水渗湿，小便不利，水肿，泄泻，淋浊，带下</li><li>利尿作用较为显着</li><li>阴虚者配阿胶、滑石等同用（如猪苓汤）</li><li>四苓散（《明医指掌》）：茯苓、泽泻、猪苓、白朮治大便溏泄，水肿，小便不利</li><li>猪苓汤（《伤寒论》）：猪苓、茯苓、泽泻、滑石、阿胶治阳明病，脉浮发热，渴欲饮水，小便不利</li></ul></li><li><p>地耳草: 甘、苦，凉。归肝、胆经。</p><ul><li>利湿退黄，清热解毒，活血消肿。</li><li>入肝胆，功能清热利湿，用治湿热黄疸</li><li>活血消肿，用治跌仆损伤</li></ul></li><li><p>垂盆草: 甘、淡、微酸，凉。归肝、胆、小肠经。</p><ul><li>利湿退黄，清热解毒。</li><li>消痈退肿。垂盆草有善解蛇毒，近年来发现本品可用治传染性肝炎</li></ul></li></ul>',2);function b(N,V){const e=t("ExternalLinkIcon");return o(),u("div",null,[c,r,l("ul",null,[p,h,d,f,m,l("li",null,[l("p",null,[l("a",x,[i("化濕、燥濕、利濕、滲濕、勝濕"),a(e)])])])]),k])}const B=n(_,[["render",b],["__file","06利水滲濕藥.html.vue"]]);export{B as default};
