import{_ as r,r as i,o as n,c as p,d as e,e as t,a as o,f as a}from"./app.3d77e012.js";const s={},c=a('<h1 id="rendering-patterns-\u6E32\u67D3\u6A21\u5F0F\u4ECB\u7D39" tabindex="-1"><a class="header-anchor" href="#rendering-patterns-\u6E32\u67D3\u6A21\u5F0F\u4ECB\u7D39" aria-hidden="true">#</a> Rendering Patterns \u6E32\u67D3\u6A21\u5F0F\u4ECB\u7D39</h1><p>\u5728\u65E5\u5E38\u7DB2\u9801\u958B\u767C\u7576\u4E2D\uFF0C\u9664\u4E86\u7A0B\u5F0F\u78BC\u76F8\u95DC\u64B0\u5BEB\u7684\u4E00\u4E9B\u6A21\u5F0F\u5916\uFF0C\u7531\u65BC\u727D\u6D89\u5230\u5BE6\u969B\u7DB2\u9801\u756B\u9762\u9084\u6709\u4E00\u500B\u91CD\u8981\u56E0\u7D20\u300C\u6E32\u67D3\u300D\uFF0C\u91DD\u5C0D\u7DB2\u9801\u756B\u9762\u6E32\u67D3\u7684\u76F8\u95DC\u958B\u767C\u5BE6\u8E10\u8207\u4F5C\u6CD5\u88AB\u7A31\u4F5C\u300C\u6E32\u67D3\u6A21\u5F0F Rendering Patterns\u300D</p><h2 id="\u6E32\u67D3\u6A21\u5F0F\u7684\u91CD\u8981\u6027" tabindex="-1"><a class="header-anchor" href="#\u6E32\u67D3\u6A21\u5F0F\u7684\u91CD\u8981\u6027" aria-hidden="true">#</a> \u6E32\u67D3\u6A21\u5F0F\u7684\u91CD\u8981\u6027</h2><p>\u70BA\u4E86\u5B8C\u6210\u9054\u6210\u66F4\u597D\u7684\u7DB2\u9801 UX \u9AD4\u9A57\uFF0C\u958B\u767C\u8005\u5011\u5E38\u6703\u8A66\u5716\u53BB\u6700\u4F73\u5316\u4E00\u4E9B\u8861\u91CF\u7DB2\u9801\u6548\u80FD\u7684\u6307\u6A19\u300CCore Web Vitals (CWV)\u300D\u5982\u4E0B\u5716\u6240\u793A\uFF0C\u85C9\u6B64\u63D0\u5347\u66F4\u597D\u7684\u4F7F\u7528\u8005\u9AD4\u9A57 <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1660456914%2Fpatterns.dev%2Fweb-vitals.png&amp;w=3840&amp;q=75" alt=""></p><p>\u800C\u70BA\u4E86\u76E1\u53EF\u80FD\u7684\u512A\u5316\u9019\u4E9B\u6307\u6A19\uFF0C\u5DE5\u7A0B\u5E2B\u5011\u7E3D\u7D50\u3001\u88FD\u4F5C\u51FA\u8A31\u591A\u80FD\u5920\u9054\u5230\u9019\u4E9B UX \u9700\u6C42\uFF0C\u4E26\u6700\u4F73\u5316 DX \u958B\u767C\u9AD4\u9A57\uFF08\u5982\u4E0B\u5716\u6240\u793A\uFF09\u7684\u5DE5\u5177\u3001\u958B\u767C\u74B0\u5883 <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F5.png&amp;w=3840&amp;q=75" alt=""></p><p>\u900F\u904E\u4F7F\u7528\u9019\u4E9B\u958B\u767C\u74B0\u5883\u3001\u6A21\u5F0F\uFF0C\u958B\u767C\u8005\u80FD\u66F4\u5FEB\u3001\u66F4\u6709\u6548\uFF0C\u4E26\u4E14\u78BA\u5BE6\u7684\u88FD\u4F5C\u51FA\u512A\u826F UX \u7684\u7522\u54C1\u4F86\uFF0C\u9019\u7A2E<code>\u7531 UX \u63A8\u5C0E\u51FA DX</code> \u7684\u904E\u7A0B\uFF0C\u50AC\u751F\u51FA\u4E86\u8A31\u591A\u4E0D\u540C\u7684\u300C\u6E32\u67D3\u6A21\u5F0F\u300D\uFF0C\u5982\u4E0B\u5716\u6240\u793A <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F6.png&amp;w=3840&amp;q=75" alt=""></p><h2 id="\u5982\u4F55\u9078\u64C7\u6E32\u67D3\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u9078\u64C7\u6E32\u67D3\u6A21\u5F0F" aria-hidden="true">#</a> \u5982\u4F55\u9078\u64C7\u6E32\u67D3\u6A21\u5F0F</h2><p>\u5404\u7A2E\u300C\u6E32\u67D3\u6A21\u5F0F\u300D\u4E0D\u50C5\u50C5\u53EA\u662F\u4E00\u500B\u6A21\u5F0F\uFF0C\u6BCF\u500B\u6E32\u67D3\u6A21\u5F0F\u80CC\u5F8C\u90FD\u6709\u4E00\u5957\u5176\u6240\u5C0D\u61C9\u91CD\u8996\u7684 UX \u9AD4\u9A57\u908F\u8F2F\uFF0C\u4E26\u6C92\u6709\u4E00\u500B\u7D55\u5C0D\u7684\u512A\u52A3\uFF08\u7576\u7136\uFF0C\u5168\u4E16\u754C\u5F88\u591A\u9AD8\u968E\u67B6\u69CB\u5DE5\u7A0B\u5E2B\u5011\uFF0C\u6B63\u6301\u7E8C\u5728\u52AA\u529B\u947D\u7814\uFF0C\u8A66\u5716\u627E\u5C0B\u51FA\u4E00\u500B\u7D55\u5C0D\u512A\u5316\u7684\u771F\u7406\u6E32\u67D3\u6A21\u5F0F\uFF0C\u4F46\u55AE\u7D14\u5C31\u76EE\u524D\u800C\u8A00\u90FD\u9084\u5728\u6BD4\u8F03\u8207\u5617\u8A66\u4E2D\uFF09</p>',8),l={href:"https://en.wikipedia.org/wiki/Hydration_(web_development)",target:"_blank",rel:"noopener noreferrer"},F=e("code",null,"full rehydration",-1),h=a('<p>\u4EE5\u4E0B\u7C21\u55AE\u4ECB\u7D39\u5E7E\u7A2E\u5E38\u898B\u7684\u300C\u6E32\u67D3\u6A21\u5F0F\u300D</p><h2 id="static-rendering-\u975C\u614B\u6E32\u67D3" tabindex="-1"><a class="header-anchor" href="#static-rendering-\u975C\u614B\u6E32\u67D3" aria-hidden="true">#</a> Static Rendering - \u975C\u614B\u6E32\u67D3</h2><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2Fimg1.png&amp;w=3840&amp;q=75" alt=""> \u975C\u614B\u5448\u73FE\u662F\u4E00\u7A2E\u7C21\u55AE\u800C\u5F37\u5927\u7684\u6A21\u5F0F\uFF0C\u7528\u4F86\u69CB\u5EFA\u5177\u6709\u5E7E\u4E4E<code>\u5373\u6642\u9801\u9762\u52A0\u8F09</code>\u7684\u5FEB\u901F\u7DB2\u7AD9\uFF0C\u6574\u500B HTML \u9801\u9762\u5728\u69CB\u5EFA\u968E\u6BB5\u751F\u6210\uFF08\u901A\u5E38\u7A31\u4F5C build\uFF09\uFF0C\u4E26\u5728\u4E0B\u4E00\u6B21\u7684 build \u524D\u5167\u5BB9\u4E0D\u6703\u66F4\u6539\uFF0C\u4E5F\u56E0\u6B64\u9019\u7A2E\u7D14\u975C\u614B\u5167\u5BB9\u80FD\u5920\u7DE9\u5B58\u5728 CDN \u6216\u908A\u7DE3\u7DB2\u7D61\u4E0A\uFF0C\u5927\u5E45\u63D0\u5347\u7DB2\u9801\u8F09\u5165\u7684\u901F\u5EA6</p><blockquote><p>\u975C\u614B\u6E32\u67D3\u9069\u7528\u65BC\u5167\u5BB9\u4E0D\u9700\u983B\u7E41\u66F4\u52D5\uFF0C\u4E26\u4E14\u4E0D\u8AD6\u89C0\u770B\u8005\u662F\u8AB0\u90FD\u4E00\u5F8B\u986F\u793A\u76F8\u540C\u5167\u5BB9\u7684\u7DB2\u9801\uFF0C\u6BD4\u5982\u96FB\u5B50\u5831\u3001\u96FB\u5B50\u5EE3\u544A\u7B49\u7B49</p></blockquote><h3 id="_1-basic-plain-static-rendering-\u57FA\u672C-\u666E\u901A\u975C\u614B\u6E32\u67D3" tabindex="-1"><a class="header-anchor" href="#_1-basic-plain-static-rendering-\u57FA\u672C-\u666E\u901A\u975C\u614B\u6E32\u67D3" aria-hidden="true">#</a> 1. Basic/Plain Static Rendering - \u57FA\u672C/\u666E\u901A\u975C\u614B\u6E32\u67D3</h3><p>\u7531\u65BC\u73FE\u4EE3\u7DB2\u9801\u4E2D\u5E38\u5E38\u9700\u8981\u4E00\u4E9B\u52D5\u614B\u7684\u81EA\u5B9A\u7FA9\u5167\u5BB9\uFF0C\u4E5F\u56E0\u6B64\u300C\u975C\u614B\u6E32\u67D3\u300D\u6A21\u5F0F\u6709\u8A31\u591A<code>\u8B8A\u9AD4</code>\uFF0C\u5176\u4E2D\u5982\u4E0A\u8FF0\u6240\u8AAA\uFF0C\u5167\u5BB9\u5728 build \u6642\u5C31\u5DF2\u7D93\u69CB\u5EFA\u5B8C\u6210\uFF0C\u7DB2\u9801\u5728\u4F7F\u7528\u8005\u8ACB\u6C42\u5F8C\u76F4\u63A5\u62FF\u5230\u975C\u614B HTML \u5167\u5BB9\u9032\u884C\u6E32\u67D3\uFF0C\u4E26\u900F\u904E bundle JS \u6574\u5408\u5C31\u5B8C\u6210\u8F09\u5165\uFF0C\u53C8\u7A31\u4F5C<code>\u300CPlain Static Rendering\u300D</code></p><p>\u9019\u7A2E\u6A21\u5F0F\u5728 <code>FCP</code>, <code>LCP</code> \u65B9\u4FBF\u8868\u73FE\u51FA\u8272\uFF0C\u56E0\u70BA\u5167\u5BB9\u5B8C\u5168\u662F\u975C\u614B\u6E32\u67D3\uFF0C\u4E5F\u5C31\u4E0D\u6703\u51FA\u73FE layout shift\uFF08\u4F48\u5C40\u504F\u79FB\uFF09</p><p>\u4F46\u5927\u591A\u6578\u60C5\u6CC1\u4E0B\uFF0C\u591A\u6578\u7684\u7DB2\u7AD9\uFF08\u5373\u4FBF\u50CF\u5B98\u65B9\u7DB2\u7AD9\uFF09\u70BA\u4E86\u65B9\u4FBF\uFF0C\u90FD\u9084\u662F\u4E0D\u53EF\u907F\u514D\u7684\u9700\u8981\u8F09\u5165\u4E00\u4E9B\u52D5\u614B\u5167\u5BB9\uFF0C\u4E5F\u56E0\u6B64\u51FA\u73FE\u4E0B\u9762\u9019\u7A2E\u8B8A\u9AD4</p><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F8.png&amp;w=3840&amp;q=75" alt=""></p><h3 id="_2-static-with-client-side-fetch" tabindex="-1"><a class="header-anchor" href="#_2-static-with-client-side-fetch" aria-hidden="true">#</a> 2. Static with Client-Side fetch</h3><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F9.png&amp;w=3840&amp;q=75" alt=""> \u5047\u8A2D\u6211\u5011\u9700\u8981\u88FD\u4F5C\u4E00\u500B\u65B0\u805E\u5217\u8868\u5340\u584A\uFF0C\u5176\u4E2D\u65B0\u805E\u5167\u5BB9\u662F\u52D5\u614B\u66F4\u52D5\u7684\u5167\u5BB9\uFF0C\u6B64\u6642\u5C31\u53EF\u4EE5\u900F\u904E\u9019\u7A2E<code>\u975C\u614B\u6E32\u67D3\u642D\u914D Client-Side fetch</code> \u7684\u6A21\u5F0F\u4F86\u8B93\u6BCF\u500B\u7528\u6236\u8ACB\u6C42\u7372\u53D6\u6700\u65B0\u52D5\u614B\u5167\u5BB9\uFF0C\u540C\u6A23\u900F\u904E\u975C\u614B\u6E32\u67D3\u7684\u65B9\u5F0F\u9810\u5148\u69CB\u5EFA\u9801\u9762\uFF0C\u4F46\u91DD\u5C0D\u9700\u8981\u52D5\u614B\u66FF\u63DB\u7684 UI \u4F4D\u7F6E\uFF0C\u63D0\u4F9B Skeleton \u9AA8\u67B6\uFF0C\u5728\u9801\u9762\u8F09\u5165\u5F8C\u4F7F\u7528\u52D5\u614B\u7684\u5167\u5BB9\u9032\u884C\u66FF\u63DB\uFF0C\u5982\u4E0B\u5716\u4F7F\u7528 <code>useSWR</code> \u7BC4\u4F8B <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F10.png&amp;w=3840&amp;q=75" alt=""></p><p>\u9019\u7A2E\u6A21\u5F0F\u4E0B\uFF0C\u7528\u6236\u8F09\u5165\u9810\u751F\u6210\u7684\u975C\u614B HTML \u756B\u9762\u5F8C\uFF0C\u6700\u521D\u6703\u770B\u5230\u52D5\u614B\u5167\u5BB9\u7684\u90E8\u5206\u5448\u73FE loading \u7684\u6A23\u5F0F\u4F48\u5C40\uFF0C\u63A5\u8457 Client-Side \u767C\u51FA API \u8ACB\u6C42\u62FF\u5230\u5167\u5BB9\uFF0C\u6700\u7D42\u52D5\u614B\u5167\u5BB9\u66FF\u63DB\u5728\u5C0D\u61C9\u7684\u4F4D\u7F6E\u4E0A\uFF0C\u6D41\u7A0B\u5982\u4E0B\u5716 <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F12.png&amp;w=3840&amp;q=75" alt=""></p><blockquote><p>\u96D6\u7136\u662F\u975C\u614B\u6E32\u67D3\uFF0C\u5C0D\u65BC <code>TTFB</code>, <code>FCP</code> \u4ECD\u6709\u4E0D\u932F\u8868\u73FE\uFF0C\u4F46 <code>LCP</code> \u5C07\u4E0D\u6703\u662F\u6700\u4F73\u72C0\u614B\uFF0C\u56E0\u70BA<code>\u6700\u5927\u5167\u5BB9</code> \u53EA\u80FD\u5728 API \u8ACB\u6C42\u5F8C\u624D\u80FD\u986F\u793A\uFF0C\u4E26\u4E14\u5982\u679C Skeleton UI \u7121\u6CD5 match \u5230\u52D5\u614B\u5167\u5BB9\u6E32\u67D3\u5F8C\u7684\u4F48\u5C40\uFF0C\u5C07\u6709\u66F4\u5927\u6A5F\u7387\u767C\u751F layout shift\uFF0C\u9664\u6B64\u4E4B\u5916\u56E0\u70BA\u6BCF\u6B21\u8F09\u5165\u90FD\u5FC5\u9808\u9032\u884C API \u8ACB\u6C42\uFF0C\u4E5F\u6703\u8B93 server \u7AEF\u904B\u884C\u6210\u672C\u66F4\u9AD8</p></blockquote><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F13.png&amp;w=3840&amp;q=75" alt=""></p><h3 id="_3-static-with-getstaticprops-pre-render" tabindex="-1"><a class="header-anchor" href="#_3-static-with-getstaticprops-pre-render" aria-hidden="true">#</a> 3. Static with getStaticProps(Pre-Render)</h3><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F14.png&amp;w=3840&amp;q=75" alt=""> \u9019\u7A2E\u6A21\u5F0F\u8207\u4E0A\u4E00\u500B\u4ECB\u7D39\u7684\u6700\u5927\u5340\u5225\u5728\u65BC\uFF0CAPI \u8ACB\u6C42\u662F\u5728 server \u7AEF build \u69CB\u5EFA\u968E\u6BB5\u9032\u884C\uFF0C\u4E26\u5C07\u5167\u5BB9\u76F4\u63A5\u7528\u65BC\u7522\u51FA\u975C\u614B HTML\uFF0C\u85C9\u6B64\u6D88\u9664 Client-Side fetch \u8ACB\u6C42\uFF0C\u6700\u7D42\u7D50\u679C\u8207 <code>Plain Static Rendering</code> \u76F8\u4F3C <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F17.png&amp;w=3840&amp;q=75" alt=""></p><blockquote><p>\u96A8\u8457\u7DB2\u7AD9\u898F\u6A21\u589E\u5927\uFF0C\u5C07\u6F38\u6F38\u4E0D\u9069\u5408\u4F7F\u7528\u9019\u6A21\u5F0F\uFF0C\u56E0\u70BA\u5728\u69CB\u5EFA\u968E\u6BB5\u8ACB\u6C42\u7684 API \u6578\u91CF\u5C07\u5C0E\u81F4 <code>build \u6642\u9593\u62C9\u9577</code>\uFF0C\u5982\u679C\u4F7F\u7528\u7684\u662F\u7B2C\u4E09\u65B9 API\uFF0C\u66F4\u53EF\u80FD\u89F8\u767C <code>request limit</code> \u6216\u662F\u9F90\u5927\u5E33\u55AE\u8CBB\u7528\uFF0C\u4E26\u4E14\u6B64\u65B9\u6CD5\u4E5F\u50C5\u662F\u9069\u7528\u65BC\u52D5\u614B\u5167\u5BB9\u672C\u8EAB\u4E5F\u4E0D\u5E38\u8B8A\u52D5\u7684\u60C5\u6CC1\u4E0B\uFF0C\u7562\u7ADF<code>\u983B\u7E41\u7684\u66F4\u65B0\u5167\u5BB9\u5C31\u8868\u793A\u983B\u7E41\u591A\u9918\u7684\u69CB\u5EFA\u6B21\u6578</code></p></blockquote><h3 id="_4-incremental-static-regeneration-isr-\u589E\u91CF\u975C\u614B\u518D\u751F" tabindex="-1"><a class="header-anchor" href="#_4-incremental-static-regeneration-isr-\u589E\u91CF\u975C\u614B\u518D\u751F" aria-hidden="true">#</a> 4. Incremental Static Regeneration\uFF08ISR\uFF09- \u589E\u91CF\u975C\u614B\u518D\u751F</h3><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F18.png&amp;w=3840&amp;q=75" alt=""> \u9019\u500B\u6A21\u5F0F\u4E3B\u8981\u89E3\u6C7A\u524D\u9762\u63D0\u5230\u7684 build \u6642\u9593\u9577\u3001\u52D5\u614B\u5167\u5BB9\u7684\u554F\u984C\uFF0C\u900F\u904E\u6DF7\u5408\u7684\u65B9\u5F0F\uFF0C\u50C5\u5448\u73FE\u67D0\u4E9B\u975C\u614B\u9801\u9762\uFF0C\u4E26\u6839\u64DA\u7528\u6236\u7684\u9700\u6C42\u52D5\u614B\u8F09\u5165\u9700\u8981\u7684\u52D5\u614B\u5167\u5BB9\uFF0C\u85C9\u6B64\u6E1B\u5C11\u4E86 build \u69CB\u5EFA\u6642\u9593\uFF0C\u4E26<code>\u5141\u8A31\u5728\u7279\u5B9A\u6642\u9593\u5F8C\u4F7F\u7DE9\u5B58\u5931\u6548\u91CD\u65B0\u69CB\u5EFA\u9801\u9762</code></p><p>\u4EE5 NextJS \u8209\u4F8B\u53EF\u4EE5\u5728 <code>getStaticProps</code> \u4E2D\u52A0\u5165 <code>revalidate</code> \u63A7\u5236 static cache \u7684\u5931\u6548\u6642\u9593\uFF0C\u4E26\u5728\u5931\u6548\u5F8C\u900F\u904E serverless function \u91CD\u65B0\u69CB\u5EFA\u4E00\u6B21\u975C\u614B\u9801\u9762 <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F22.png&amp;w=3840&amp;q=75" alt=""></p><blockquote><p>\u7528\u6236\u5982\u679C\u53D6\u5F97\u4E00\u500B\u5931\u6548\u7684\u975C\u614B\u9801\u9762\u6642\uFF0C\u6700\u521D\u4ECD\u5C07\u770B\u5230\u820A\u7684\u9801\u9762\uFF0C\u540C\u6642\u89F8\u767C server \u91CD\u65B0\u69CB\u5EFA\uFF0C\u4E00\u65E6\u9801\u9762\u5728 server \u7AEF\u91CD\u65B0\u69CB\u5EFA\u5B8C\u6210\uFF0C\u820A\u7684\u7DE9\u5B58\u624D\u6703\u5931\u6548\u4E26\u4F7F\u7528\u6700\u65B0\u751F\u6210\u7684\u9801\u9762\u9032\u884C\u66F4\u65B0</p></blockquote><p>\u4F7F\u7528\u589E\u91CF\u975C\u614B\u91CD\u65B0\u751F\u6210\uFF0C\u6211\u5011\u53EF\u4EE5\u901A\u904E\u6BCF\u9694\u5E7E\u79D2\u81EA\u52D5\u91CD\u65B0\u9A57\u8B49\u9801\u9762\u4F86\u986F\u793A\u52D5\u614B\u5167\u5BB9\u3002\u96D6\u7136\u5DF2\u7D93\u89E3\u6C7A\u4E86\u8A31\u591A\u554F\u984C\uFF0C\u4F46\u4ECD\u7136\u6709\u7F8E\u4E2D\u4E0D\u8DB3\u7684\u5730\u65B9</p><ul><li>\u300C\u52D5\u614B\u5167\u5BB9\u53EF\u80FD\u4E0D\u6703\u50CF\u6211\u5011\u5B9A\u7FA9\u7684\u9593\u9694\u90A3\u6A23\u983B\u7E41\u66F4\u65B0\uFF0C\u9019\u5C07\u5C0E\u81F4<code>\u4E0D\u5FC5\u8981\u7684\u9801\u9762\u91CD\u65B0\u751F\u6210</code>\u548C<code>\u7DE9\u5B58\u5931\u6548</code>\uFF0C\u9032\u800C\u5C0E\u81F4\u66F4\u9AD8\u7684\u670D\u52D9\u5668\u6210\u672C\u300D</li></ul><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F23.png&amp;w=3840&amp;q=75" alt=""></p><h3 id="_5-on-demand-isr" tabindex="-1"><a class="header-anchor" href="#_5-on-demand-isr" aria-hidden="true">#</a> 5. On-demand ISR</h3><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F24.png&amp;w=3840&amp;q=75" alt=""> \u70BA\u4E86\u89E3\u6C7A\u4E0A\u8FF0\u63D0\u5230 ISR \u7684 <code>revalidate</code> \u554F\u984C\uFF0C\u900F\u904E\u5B9A\u7FA9\u300C\u7279\u5B9A\u7684 event\u300D\u4F86 trigger \u9801\u9762\u91CD\u65B0\u69CB\u5EFA\uFF0C\u800C\u4E0D\u662F\u55AE\u7D14\u900F\u904E\u4E00\u500B\u56FA\u5B9A\u6642\u9593\u4F86\u9032\u884C\uFF0C\u5982\u4E0B\u5716\u6240\u793A\uFF0C\u7531\u5916\u90E8\u89F8\u767C\uFF08server\uFF09\u91DD\u5C0D\u67D0\u4E00\u500B\u6307\u5B9A\u8DEF\u5F91\u9801\u9762\u9032\u884C revalidate <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F25.png&amp;w=3840&amp;q=75" alt=""></p><p>\u5982\u6B64\u4E00\u4F86\u907F\u514D\u6389\u539F\u672C ISR \u6A21\u5F0F\u591A\u9918\u7684 revalidate \u6548\u80FD\u6D88\u8017 <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F26.png&amp;w=3840&amp;q=75" alt=""></p><h3 id="\u975C\u614B\u6E32\u67D3\u7E3D\u7D50" tabindex="-1"><a class="header-anchor" href="#\u975C\u614B\u6E32\u67D3\u7E3D\u7D50" aria-hidden="true">#</a> \u975C\u614B\u6E32\u67D3\u7E3D\u7D50</h3><p>\u7E3D\u9AD4\u800C\u8A00\uFF0C\u975C\u614B\u751F\u6210\u662F\u4E00\u7A2E\u9AD8\u6548\u7684\u6A21\u5F0F\uFF0C\u5176\u8B8A\u9AD4\uFF0C\u5C24\u5176\u662F ISR\uFF0C\u53EF\u4EE5\u6DB5\u84CB\u5404\u7A2E\u4F7F\u7528\u60C5\u5883\uFF0C\u4F7F\u6211\u5011\u80FD\u5920\u4EE5\u5408\u7406\u7684\u6210\u672C\u64C1\u6709\u59CB\u7D42\u5728\u7DDA\u7684\u5FEB\u901F\u52D5\u614B\u7DB2\u7AD9\u3002</p><p>\u4F46\u5C0D\u65BC\u67D0\u4E9B\u5834\u666F\uFF0C\u975C\u614B\u6E32\u67D3\u4E26\u4E0D\u662F\u6700\u4F73\u9078\u64C7\uFF0C\u4F8B\u5982\u5C0D\u65BC\u6BCF\u500B\u7528\u6236\u90FD\u4E0D\u540C\u7684\u9AD8\u5EA6\u52D5\u614B\u3001\u500B\u6027\u5316\u7684\u9801\u9762</p><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F27.png&amp;w=3840&amp;q=75" alt=""></p><h2 id="server-side-rendering-\u4F3A\u670D\u5668\u7AEF\u6E32\u67D3" tabindex="-1"><a class="header-anchor" href="#server-side-rendering-\u4F3A\u670D\u5668\u7AEF\u6E32\u67D3" aria-hidden="true">#</a> Server Side Rendering - \u4F3A\u670D\u5668\u7AEF\u6E32\u67D3</h2><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F28.png&amp;w=3840&amp;q=75" alt=""></p><p>\u4F3A\u670D\u6C23\u5668\u7AEF\u6E32\u67D3\uFF0C\u6703\u70BA\u6BCF\u500B\u8ACB\u6C42\u751F\u6210 HTML\uFF0C\u9069\u5408\u5305\u542B\u9AD8\u5EA6\u5BA2\u5236\u5316\u8CC7\u6599\u7684\u9801\u9762\uFF0C\u4F8B\u5982\u57FA\u65BC\u7528\u6236cookie\u6216\u7528\u6236\u8ACB\u6C42\u4F86\u7372\u5F97\u7684\u4EFB\u4F55\u8CC7\u6599\uFF0C\u53E6\u5916\u4E5F\u9069\u5408\u7528\u65BC\u9700\u8981\u963B\u64CB\u67D0\u4E9B\u9801\u9762\u7684\u5834\u5408\uFF0C\u6BD4\u5982\u300C\u8EAB\u4EFD\u9A57\u8B49\u300D\u7B49\u3002</p><p>\u500B\u6027\u5316\u5100\u8868\u677F\u662F\u9AD8\u5EA6\u52D5\u614B\u5167\u5BB9\u7684\u4E00\u500B\u5F88\u597D\u7684\u4F8B\u5B50\uFF0C\u5167\u5982\u6839\u64DA cookie \u4E2D\u95DC\u65BC\u7528\u6236\u7684\u8EAB\u4EFD\u8CC7\u8A0A\u7372\u53D6\u5C0D\u61C9\u7684\u52D5\u614B\u5167\u5BB9\uFF0C\u4E5F\u80FD\u8F03\u597D\u7684\u5BE6\u73FE\u67D0\u4E9B\u654F\u611F\u8CC7\u8A0A\u7684\u5B89\u5168\u9700\u6C42\uFF08\u7121\u6CD5\u88AB\u5176\u4ED6\u4EBA\u770B\u5230\uFF09\uFF0C\u6BD4\u5982 Next.js \u5141\u8A31\u6211\u5011\u4F7F\u7528 <code>getServerSideProps</code> \u65B9\u6CD5\u5728\u4F3A\u670D\u5668\u7AEF\u6E32\u67D3\u9801\u9762\u3002\u8A72\u65B9\u6CD5\u91DD\u5C0D\u6BCF\u500B\u8ACB\u6C42\u5728\u4F3A\u670D\u5668\u4E0A\u904B\u884C\uFF0C\u6700\u7D42\u5C07\u8FD4\u56DE\u7684\u5167\u5BB9\u50B3\u905E\u5230\u9801\u9762\u4EE5\u751F\u6210 HTML</p><p>\u6BCF\u7576\u7528\u6236\u8ACB\u6C42\u9801\u9762\u6642\uFF0C<code>getServerSideProps</code> \u904B\u884C\uFF0C\u8FD4\u56DE\u7528\u65BC\u751F\u6210\u9801\u9762\u7684\u8CC7\u6599\uFF0C\u63A5\u8457\u5728\u524D\u7AEF\u9801\u9762\u4E0A\u5448\u73FE HTML \u5167\u5BB9\uFF0C\u4E26\u53EF\u80FD\u63A5\u8457\u518D\u53BB\u8ACB\u6C42 js \u52D5\u614B\u5167\u5BB9\u4E26\u6839\u64DA\u8FD4\u56DE\u5167\u5BB9\u9032\u884C\u6BD4\u5C0D\u8ABF\u6574\uFF0C\u751F\u6210\u7684 HTML \u5167\u5BB9\u5C0D\u65BC\u6BCF\u500B\u8ACB\u6C42\u90FD\u662F\u552F\u4E00\u7684\uFF0C\u4E0D\u61C9\u7531 CDN \u7DE9\u5B58\u3002 <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F31.png&amp;w=3840&amp;q=75" alt=""></p><p>\u5C0D\u65BC\u975C\u614B\u548C\u670D\u52D9\u5668\u7AEF\u6E32\u67D3\u975E\u5E38\u76F8\u4F3C\u3002FCP \u5E7E\u4E4E\u7B49\u65BC LCP\uFF0C\u8F15\u9B06\u907F\u514D\u4F48\u5C40\u8B8A\u5316\uFF0C\u56E0\u70BA\u521D\u59CB\u9801\u9762\u52A0\u8F09\u5F8C\u6C92\u6709\u52D5\u614B\u5167\u5BB9\u52A0\u8F09\uFF0C\u53EA\u662F\u670D\u52D9\u5668\u6E32\u67D3\u9801\u9762\u7684 TTFB \u660E\u986F\u9577\u65BC\u975C\u614B\u6E32\u67D3\uFF0C\u56E0\u70BA\u9801\u9762\u662F\u5728\u6BCF\u6B21\u8ACB\u6C42\u6642\u5F9E\u982D\u958B\u59CB\u751F\u6210\u3002\u8A55\u50F9\u5982\u4E0B\uFF1A <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F32.png&amp;w=3840&amp;q=75" alt=""></p><p>\u96D6\u7136\u4F3A\u670D\u5668\u7AEF\u6E32\u67D3\u53EF\u4EE5\u66F4\u597D\u7684\u505A\u5230\u5BA2\u88FD\u5316\u52D5\u614B\u9801\u9762\uFF0C\u4F46\u70BA\u4E86\u7372\u5F97\u826F\u597D\u7684\u7528\u6236\u9AD4\u9A57\u4E26\u964D\u4F4E\u670D\u52D9\u5668\u6210\u672C\uFF0C\u4EE5\u4E0B\u662F\u4E00\u4E9B\u5728\u4F7F\u7528 SSR \u6642\u9700\u8981\u6CE8\u610F\u7684\u5730\u65B9\uFF1A</p><ol><li>getServerSideProps \u7684\u57F7\u884C\u6642\u9593<br> \u9801\u9762\u5FC5\u9808\u5728 getServerSideProps \u4E2D\u7684\u8CC7\u6599\u53EF\u7528\u4E4B\u5F8C\u624D\u6703\u958B\u59CB\u751F\u6210\u3002\u56E0\u6B64\uFF0C\u6211\u5011\u5FC5\u9808\u78BA\u4FDD getServerSideProps \u65B9\u6CD5\u4E0D\u6703\u904B\u884C\u592A\u9577\u6642\u9593</li><li>\u5C07 Database \u90E8\u7F72\u5728\u8207 serverless \u529F\u80FD\u76F8\u540C\u7684\u5340\u57DF\u4E2D<br> \u50B3\u7D71\u90E8\u7F72\u53EF\u80FD\u6703\u8B93\u524D\u7AEF\u5F9E\u516C\u5171\u5916\u7DB2\u8ACB\u6C42 API\uFF0C\u4F46\u9019\u7A2E\u65B9\u5F0F\u5FC5\u9808\u7D93\u904E\u4E00\u9023\u4E32\u7684 DNS \u67E5\u8A62\u3001cluster\u3001load balancer \u67E5\u8A62\u7B49\u7B49\uFF0C\u97FF\u61C9\u901F\u5EA6\u975E\u5E38\u7DE9\u6162\uFF0C\u900F\u904E\u628A\u76F8\u95DC\u7684\u670D\u52D9\u90E8\u7F72\u5728\u4E00\u8D77\uFF08\u540C\u500B cluster\uFF09\uFF0C\u85C9\u6B64\u63D0\u5347\u52D5\u614B\u5167\u5BB9\u7372\u53D6\u7684\u901F\u5EA6\uFF0C\u5E38\u898B\u7684\u65B9\u5F0F\u662F\u900F\u904E cluster \u5167\u7684\u7DB2\u7D61\u9032\u884C\u9023\u7DDA\uFF0C\u53EF\u4EE5\u5927\u5E45\u63D0\u5347\u8CC7\u6599\u7372\u53D6\u901F\u5EA6</li><li>\u5C07 <code>Cache-control</code> header \u6DFB\u52A0\u5230 response \u4E2D</li><li>\u5347\u7D1A Server \u786C\u9AD4\u898F\u683C</li></ol><h3 id="edge-ssr-http-streaming" tabindex="-1"><a class="header-anchor" href="#edge-ssr-http-streaming" aria-hidden="true">#</a> Edge SSR + HTTP Streaming</h3><ul><li>\u4F7F\u7528 serverless \u529F\u80FD\uFF0C\u6211\u5011\u5728\u670D\u52D9\u5668\u7AEF\u751F\u6210\u6574\u500B\u9801\u9762\uFF0C\u4E26\u7B49\u5F85\u6574\u500B\u5305\u5728\u5BA2\u6236\u7AEF\u4E0A\u52A0\u8F09\u548C\u89E3\u6790\uFF0C\u7136\u5F8C\u624D\u80FD\u958B\u59CB\u6C34\u5408\u4F5C\u7528\u3002</li><li>\u4F7F\u7528 Edge SSR\uFF0C\u6211\u5011\u53EF\u4EE5\u5728\u6E96\u5099\u5C31\u7DD2\u5F8C\u7ACB\u5373\u6D41\u5F0F\u50B3\u8F38\u6587\u6A94\u7684\u5404\u500B\u90E8\u5206\uFF0C\u4E26\u7D30\u7C92\u5EA6\u5730\u6DF7\u5408\u9019\u4E9B\u7D44\u4EF6\uFF0C\u5148\u52A0\u8F09\u5B8C\u7684\u5148\u6E32\u67D3\uFF0C\u6E1B\u5C11\u4E86\u7528\u6236\u7684\u7B49\u5F85\u6642\u9593 <img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F40.png&amp;w=3840&amp;q=75" alt=""></li></ul><p>Streaming SSR \u9084\u652F\u6301 React \u670D\u52D9\u5668\u7D44\u4EF6\u3002 Edge SSR \u8207 React Server Components \u7684\u7D50\u5408\u53EF\u4EE5\u8B93\u6211\u5011\u5728\u975C\u614B\u548C\u670D\u52D9\u5668\u6E32\u67D3\u4E4B\u9593\u6709\u4E00\u500B\u6F02\u4EAE\u7684\u6DF7\u5408\u9AD4\uFF0CReact Server Components \u5141\u8A31\u63A1\u53D6\u300C\u90E8\u5206\u300D\u5167\u5BB9\u4F3A\u670D\u5668\u7AEF\u6E32\u67D3\uFF0C\u5047\u5982\u9700\u8981\u986F\u793A\u500B\u4EBA\u5316\u7684\u8CC7\u8A0A\u5217\u8868\u5340\u584A\uFF0C\u5C31\u53EF\u4EE5\u9078\u64C7\u50C5\u5728\u670D\u52D9\u5668\u7AEF\u6E32\u67D3\u5217\u8868\u7D44\u4EF6\u200B\u200B\uFF0C\u800C\u5728\u5BA2\u6236\u7AEF\u6E32\u67D3\u5176\u9918\u90E8\u5206\uFF0C\u800C\u4E0D\u5FC5\u5728\u670D\u52D9\u5668\u4E0A\u6E32\u67D3\u6574\u500B\u9801\u9762\uFF0C\u540C\u6642\u7372\u5F97\u4E86\u975C\u614B\u6E32\u67D3\u3001\u4F3A\u670D\u5668\u6E32\u67D3\u7684\u512A\u52E2\u3002</p><p><img src="https://www.patterns.dev/_next/image?url=https%3A%2F%2Fres.cloudinary.com%2Fddxwdqwkr%2Fimage%2Fupload%2Ff_auto%2Fv1658990025%2Fpatterns.dev%2F42.png&amp;w=3840&amp;q=75" alt=""></p><h2 id="\u7D50\u8AD6" tabindex="-1"><a class="header-anchor" href="#\u7D50\u8AD6" aria-hidden="true">#</a> \u7D50\u8AD6</h2><p>\u96D6\u7136 Edge SSR \u53EF\u4EE5\u63D0\u5347\u6574\u9AD4\u6548\u80FD\u7684\u80FD\u529B\uFF0C\u4F46\u5C0D\u65BC\u975E\u5E38\u52D5\u614B\u7684\u7DB2\u7AD9\uFF0C\u4ECD\u7136\u5EFA\u8B70\u4F7F\u7528\u5B8C\u5168 rehydration\uFF08\u518D\u6C34\u5316\uFF09\u7684 Client Side Rendering (CSR)\uFF0C\u5728\u9019\u4E9B\u7DB2\u7AD9\u4E2D\uFF0C\u5C4F\u5E55\u4E0A\u7684\u6BCF\u500B\u7D44\u4EF6\u90FD\u53EF\u80FD\u6839\u64DA\u7528\u6236\u4EA4\u4E92\u800C\u6539\u8B8A</p><p>\u4F9D\u64DA\u5BE6\u969B\u60C5\u6CC1\uFF0C\u6709\u4E9B\u6A21\u5F0F\u53EF\u80FD\u6BD4\u5176\u4ED6\u6A21\u5F0F\u66F4\u5408\u9069</p><table><thead><tr><th></th><th>Portfolio</th><th>Content</th><th>Storefront</th><th>Social Network</th><th>Immersive</th></tr></thead><tbody><tr><th>Holotype</th><td>Personal Blog</td><td>CNN</td><td>Amazon</td><td>Facebook</td><td>Figma</td></tr><tr><th>Interactivity</th><td>Minimal</td><td>Linked Articles</td><td>Purchase</td><td>Multi-Point, Real-time</td><td>Everything</td></tr><tr><th>Session Depth</th><td>Shallow</td><td>Shallow</td><td>Shallow - Medium</td><td>Extended</td><td>Deep</td></tr><tr><th>Values</th><td>Simplicity</td><td>Discover-ability</td><td>Load Performance</td><td>Dynamicism</td><td>Immersiveness</td></tr><tr><th>Routing</th><td>Server</td><td>Server, Hybrid</td><td>Hybrid, Transitional</td><td>Transitional, Client</td><td>Client</td></tr><tr><th>Rendering</th><td>Static</td><td>Static, SSR</td><td>Static, SSR</td><td>SSR</td><td>CSR</td></tr><tr><th>Hydration</th><td>None</td><td>Progressive, Partial</td><td>Partial, Resumable</td><td>Any</td><td>None (CSR)</td></tr><tr><th>Example Framework</th><td>11ty</td><td>Astro, Elder</td><td>Marko, Qwik, Hydrogen</td><td>Next, Remix</td><td>Create React App</td></tr></tbody></table>',47);function m(w,u){const d=i("ExternalLinkIcon");return n(),p("div",null,[c,e("p",null,[t("Chrome \u5718\u968A\u63A8\u85A6\u958B\u767C\u8005\u4F7F\u7528\u975C\u614B\u6216\u4F3A\u670D\u5668\u7AEF\u6E32\u67D3\u6A21\u5F0F\uFF0C\u800C\u975E "),e("a",l,[F,o(d)]),t(" \u7684\u65B9\u5F0F")]),h])}const v=r(s,[["render",m],["__file","introduction.html.vue"]]);export{v as default};
