import{_ as i,o as a,c as l,f as e}from"./app-5f5a908c.js";const h={},r=e('<h1 id="配伍" tabindex="-1"><a class="header-anchor" href="#配伍" aria-hidden="true">#</a> 配伍</h1><h2 id="感冒藥" tabindex="-1"><a class="header-anchor" href="#感冒藥" aria-hidden="true">#</a> 感冒藥</h2><ul><li>紫蘇：3-9g</li><li>防風：3-9g</li><li>炙甘草：1.5g-9g</li><li>熟地黃：6-18g</li></ul><h3 id="治風寒風熱發熱頭暈頭痛" tabindex="-1"><a class="header-anchor" href="#治風寒風熱發熱頭暈頭痛" aria-hidden="true">#</a> 治風寒風熱發熱頭暈頭痛</h3><ul><li>川芎：3-9g (高血壓頭痛、肝火頭痛、陰虛火旺忌用)</li><li>柴胡: 3-10g(退熱宜生用)</li><li>葛根: 10-15g(退熱生津宜生用)</li></ul><h3 id="止咳化痰" tabindex="-1"><a class="header-anchor" href="#止咳化痰" aria-hidden="true">#</a> 止咳化痰</h3><ul><li>前胡: 3-9g(用於宣散風熱降氣，氣虛血少症禁用)</li></ul><h3 id="溫熱毒、溫病發熱頭痛" tabindex="-1"><a class="header-anchor" href="#溫熱毒、溫病發熱頭痛" aria-hidden="true">#</a> 溫熱毒、溫病發熱頭痛</h3><ul><li>大青葉：10-15g(脾胃虛寒者忌用), 抗菌、抗病毒、解熱消炎</li></ul><h3 id="咽喉腫痛" tabindex="-1"><a class="header-anchor" href="#咽喉腫痛" aria-hidden="true">#</a> 咽喉腫痛</h3><ul><li>板藍根：10-15g, 抗菌、抗病毒、解熱消炎</li><li>玄蔘</li></ul><h3 id="肺熱咳嗽、熱毒溫病" tabindex="-1"><a class="header-anchor" href="#肺熱咳嗽、熱毒溫病" aria-hidden="true">#</a> 肺熱咳嗽、熱毒溫病</h3><ul><li>魚腥草：15-30g(不宜久煎)</li><li>蒲公英：10-30g</li></ul><h2 id="陰虛火旺" tabindex="-1"><a class="header-anchor" href="#陰虛火旺" aria-hidden="true">#</a> 陰虛火旺</h2><ul><li>炙甘草：1.5g-9g</li><li>熟地黃：6-18g</li><li>生地黃：6-18g</li><li>麥門冬：10-15g（微寒，養陰潤肺、益胃生津、清心除煩）</li><li>天門冬：10-15g (極寒，「上品」之藥，並有「久服輕身益氣延年」的記載)</li></ul><h3 id="潤心肺、清虛痰" tabindex="-1"><a class="header-anchor" href="#潤心肺、清虛痰" aria-hidden="true">#</a> 潤心肺、清虛痰</h3><ul><li>貝母：3-10g</li><li>知母：6-12g(潤寒，脾虛便溏者不宜用)</li></ul><h3 id="溫毒發斑、津傷便秘、目赤咽痛" tabindex="-1"><a class="header-anchor" href="#溫毒發斑、津傷便秘、目赤咽痛" aria-hidden="true">#</a> 溫毒發斑、津傷便秘、目赤咽痛</h3><ul><li>牡丹皮：6-12g</li><li>赤芍：6-15g (抗血小板凝集，抗血栓，血虛寒、經閉、出血不宜用)</li><li>玄參：9-15g(脾胃虛寒，食少便溏者不宜服用)</li></ul><blockquote><p>瀉火解毒力較強，不宜久服</p></blockquote><h2 id="四物" tabindex="-1"><a class="header-anchor" href="#四物" aria-hidden="true">#</a> 四物</h2><ul><li>當歸：7.5-10g</li><li>川芎：7.5-10g</li><li>白芍：7.5-10g</li><li>熟地：7.5-10g</li></ul><h2 id="健脾利濕" tabindex="-1"><a class="header-anchor" href="#健脾利濕" aria-hidden="true">#</a> 健脾利濕</h2><ul><li>半夏: 3-10g (治風痰熱痰寒痰濕痰，脾不化湿、聚而成痰者)</li><li>茯苓: 10-15g(治寒熱虛實各種水腫)</li></ul><h2 id="風濕-癲癇、中風、破傷風" tabindex="-1"><a class="header-anchor" href="#風濕-癲癇、中風、破傷風" aria-hidden="true">#</a> 風濕（癲癇、中風、破傷風）</h2><ul><li>天南星</li></ul><h2 id="止嘔" tabindex="-1"><a class="header-anchor" href="#止嘔" aria-hidden="true">#</a> 止嘔</h2><ul><li>半夏</li></ul><h2 id="利水消腫" tabindex="-1"><a class="header-anchor" href="#利水消腫" aria-hidden="true">#</a> 利水消腫</h2><ul><li>昆布: 6-20g</li></ul><h2 id="補氣固表止汗" tabindex="-1"><a class="header-anchor" href="#補氣固表止汗" aria-hidden="true">#</a> 補氣固表止汗</h2><h3 id="玉屏風散" tabindex="-1"><a class="header-anchor" href="#玉屏風散" aria-hidden="true">#</a> 玉屏風散</h3><p>脈浮虛軟，或體虛易感風邪者</p><ul><li>黃耆: 10g</li><li>白朮: 10g</li><li>防風: 10g</li></ul><h4 id="尋麻疹" tabindex="-1"><a class="header-anchor" href="#尋麻疹" aria-hidden="true">#</a> 尋麻疹</h4><ul><li>當歸, 白芍</li></ul><h2 id="養心湯加減-補氣行氣健脾" tabindex="-1"><a class="header-anchor" href="#養心湯加減-補氣行氣健脾" aria-hidden="true">#</a> 養心湯加減（補氣行氣健脾）</h2><ul><li>主養血、養心安神：柏子仁(2g)、當歸(3g)、白芍(3g)</li><li>潤燥養陰：熟地(3g)、生地(3g)、知母(2g)</li><li>健脾利濕：茯苓(3g)、炙甘草(1g)</li><li>補氣：黃耆(3g)、紅棗(5顆)</li><li>活血行氣：川穹(3g)</li></ul>',38),d=[r];function n(t,u){return a(),l("div",null,d)}const s=i(h,[["render",n],["__file","配伍.html.vue"]]);export{s as default};
